package dasar.conditional;

public class KelasIfElseDasarString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String cuaca = "Cerah";
		
		if (cuaca != "Cerah") {
			System.out.println("Tidak Banjir");
		} else {
			System.out.println("Ada kemungkinan Banjir");
		}
	}

}
