package dasar.conditional;

public class KelasSwitchDasar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int day = 5;
		
		switch (day) {
		case 3:
			System.out.println("Makan Bareng");
			break;
		case 4:
			System.out.println("Ngantor");
			break;
		case 5:
			System.out.println("Nonton");
			break;
		default:
			System.out.println("Gabut");
			break;
		}

	}

}
