package dasar.loop;

public class KelasForKondisi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String buah = "Jeruk";
		String sayur = "Kangkung";
		for (int i = 0; i < 3; i++) {
			if (i == 1) {
				System.out.println(buah+" "+ i);
			} else {
				System.out.println(sayur+" "+ i);
			}
		}
		
	}

}
