package dasar.loop.nestedloop;

public class KelasNestedLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String buah = "Mangga";
		
		for (int i = 0; i < 3; i++) {
			System.out.print(buah + "\t");
		}
		
		System.out.println();
		
		for (int i = 0; i < 3; i++) {
			System.out.print(buah + "\t");
		}
	}

}
