package dasar.loop.nestedloop;

public class KelasNestedLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Mangga Mangga Mangga
		// Mangga Mangga Mangga
		
		for (int i = 0; i < 3; i++) { // induk/baris
			
			int angka = 9;
			
			for (int j = 0; j < 3; j++) { // kolom
				System.out.print(angka + " ");
			}
			
			System.out.println();
		}
		
	}

}
