package dasar.operator;

public class KelasOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// - , + , == , < , > , <= , !=, >=, *, /
		
		// Pertambahan int + Long
		int ia = 10;
		long ba = 9L;
		Long hasil = ia + ba; 
		System.out.println(hasil);
		
		// Pertambahan int + Double
		int io = 97;
		Double fa = 50.20;
		Double hasil2 = io + fa;
		System.out.println(hasil2);
		
		// Pengurangan Double - Float
		Double da = 80.9;
		Float di = 70f;
		Double hasil3 = da-di;
		System.out.println(hasil3);
		
		// Perkalian Int * Long
		int ab = 80;
		Long bc = 9L;
		Long hasil4 = ab*bc;
		System.out.println(hasil4);
	}

}
